group.script:
function main() {
    var h;

    // handles minimum length
    if (params.length < 15) { params.length = 15; }

    // parts
    h = cylinder({
        r1: 1,
        r2: 2,
        h: params.length,
        fn: 6
    }).scale([1, 0.4, 1]);

    // handle positioning
    h = h.rotateX(90);
    h = h.translate([0, -12, 3]);

    return h;
}

function main() {
    var head, diff, flatten;

    // parts
    head = sphere(5).scale([1, 3, 0.2]);

    diff = cube({
        size: [10, 30, 5], 
        center: true
    });

    flatten = cube({
        size: [10, 30, 3],
        center: true
    });

    // transformations
    diff = diff.translate([6, 0, 0]);

    head = difference(
        head, 
        diff, 
        flatten.translate([0, 0, 2]),
        flatten.translate([0, 0, -2])
    );

    head = head.translate([0, 0, 3]);

    return head;
}
